using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace NeuroOCR
{
    class NeuroNet : Dictionary<String, Neuro>
    {
        public NeuroNet()
        {
            String a = System.IO.File.ReadAllText("ocr.file");
            for (int i = 0; i < a.Length; i++)
            {
                this.Add(a[i].ToString(), new Neuro(a[i].ToString()));
            }
        }
        public void Clear(String el) {
            try
            {
                this[el].Clear();
            } catch { }
        }
        public void MegaClever(TableOCR img, String el)
        {
            foreach (KeyValuePair<String, Neuro> pair in this)
            {
                pair.Value.Clever(img, (el == pair.Key));
            }
        }
        public void SoftClever(TableOCR img, String el)
        {
            try
            {
                String recog = Recognize(img);
                if (recog != el)
                {
                    this[el].Clever(img, true);
                    this[recog].Clever(img, false);
                }
            }
            catch { }
        }
        public void HelpClever(TableOCR img, String el)
        {
            try
            {
                String recog = Recognize(img);
                if (recog != el)
                {
                    this[el].Clever(img, true);
                }
            }
            catch { }
        }
        public void HardClever(TableOCR img, String el)
        {
            try
            {
                this[el].Clever(img, true);
            }
            catch { }
        }
        public void HardNotClever(TableOCR img, String el)
        {
            this[el].Clever(img, false);
        }
        public String Recognize(TableOCR img)
        {
            OCRPair pair = new OCRPair(Int16.MinValue, "");
            foreach (KeyValuePair<String, Neuro> p in this)
            {
                pair = (pair+p.Value.Work(img));
            }
            return pair.c;
        }
        public void Flush()
        {
            foreach (KeyValuePair<String, Neuro> sn in this)
            {
                sn.Value.Flush();
            }
        }
        public void Hard()
        {
            foreach (KeyValuePair<String, Neuro> sn in this)
            {
                sn.Value.Hard();
            }
        }

